public final class EliminarGradoProteccionAction extends ActionSession {

    protected static Log m_Log =
            LogFactory.getLog(EliminarGradoProteccionAction.class.getName());

    public ActionForward performSession(ActionMapping mapping, ActionForm form, HttpServletRequest request,
                                        HttpServletResponse response) throws IOException, ServletException {

        m_Log.info("EliminarGradoProteccionAction.perform");
        String[] params = null;
        HttpSession session = request.getSession();

        if (session.getAttribute("usuario") != null){
            UsuarioValueObject usuarioVO = (UsuarioValueObject)session.getAttribute("usuario");
            params = usuarioVO.getParamsCon();
        }

        MantenimientoGradosProteccionForm gradosProteccionForm = (MantenimientoGradosProteccionForm) form;

        //Comprobamos que el gradoProteccion no esta siendo utilizado
        Vector registros = RegistroManager.getInstance().findByGradoProteccion(gradosProteccionForm.getCodigo(), params);
        if (!registros.isEmpty()) {
            request.setAttribute("gradosProteccion", ((MantenimientoGradosProteccionForm)
                session.getAttribute("MantenimientoGradosProteccionForm")).getGradosProteccion());
            return (mapping.findForward("noDelete"));
        }
        
        MantGradoProteccionValueObject gradoProteccionVO = new MantGradoProteccionValueObject(gradosProteccionForm.getCodigo(), gradosProteccionForm.getDescripcion());
        MantGradoProteccionManager.getInstance().delete(gradoProteccionVO, params);

        Iterator gradosProteccionIt = ((MantenimientoGradosProteccionForm)
                session.getAttribute("MantenimientoGradosProteccionForm")).getGradosProteccion().iterator();
        Collection gradosProteccion = new ArrayList();
        while (gradosProteccionIt.hasNext()) {
            MantenimientoGradoProteccionForm mantGradoProteccionForm = (MantenimientoGradoProteccionForm) gradosProteccionIt.next();
            if (!mantGradoProteccionForm.getCodigo().equals(gradosProteccionForm.getCodigo())) {
                gradosProteccion.add(mantGradoProteccionForm);
            }
        }

        gradosProteccionForm.setGradosProteccion(gradosProteccion);

        session.setAttribute("MantenimientoGradosProteccionForm", gradosProteccionForm);
        return (mapping.findForward("default"));
    }
}